<?php
class installerPtw {
	static public $update_to_version_method = '';
	static private $_firstTimeActivated = false;
	static public function init() {
		global $wpdb;
		// $wpdb->show_errors(true);
		$wpPrefix = $wpdb->prefix; /* add to 0.0.3 Versiom */
		require_once(ABSPATH . 'wp-admin/includes/upgrade.php');
		$current_version = get_option($wpPrefix. PTW_DB_PREF. 'db_version', 0);
		if(!$current_version)
			self::$_firstTimeActivated = true;
		/**
		 * modules 
		 */
		//dbPtw::query("DROP TABLE @__modules");
		if (!dbPtw::exist("@__modules")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE IF NOT EXISTS `@__modules` (
			  `id` smallint(3) NOT NULL AUTO_INCREMENT,
			  `code` varchar(32) NOT NULL,
			  `active` tinyint(1) NOT NULL DEFAULT '0',
			  `type_id` tinyint(1) NOT NULL DEFAULT '0',
			  `label` varchar(64) DEFAULT NULL,
			  `ex_plug_dir` varchar(255) DEFAULT NULL,
			  PRIMARY KEY (`id`),
			  UNIQUE INDEX `code` (`code`)
			) DEFAULT CHARSET=utf8;"));
			dbPtw::query("INSERT INTO `@__modules` (id, code, active, type_id, label) VALUES
				(NULL, 'adminmenu',1,1,'Admin Menu'),
				(NULL, 'options',1,1,'Options'),
				(NULL, 'user',1,1,'Users'),
				(NULL, 'pages',1,1,'Pages'),
				(NULL, 'templates',1,1,'templates'),
				(NULL, 'promo',1,1,'promo'),
				(NULL, 'admin_nav',1,1,'admin_nav'),
				(NULL, 'mail',1,1,'mail'),
				
				(NULL, 'tables',1,1,'tables');");
		}
		/**
		 *  modules_type 
		 */
		if(!dbPtw::exist("@__modules_type")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE IF NOT EXISTS `@__modules_type` (
			  `id` smallint(3) NOT NULL AUTO_INCREMENT,
			  `label` varchar(32) NOT NULL,
			  PRIMARY KEY (`id`)
			) AUTO_INCREMENT=4 DEFAULT CHARSET=utf8;"));
			dbPtw::query("INSERT INTO `@__modules_type` VALUES
				(1,'system'),
				(6,'addons');");
		}
		if (!dbPtw::exist("@__tables")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE IF NOT EXISTS `@__tables` (
				`id` INT(11) NOT NULL AUTO_INCREMENT,
				`unique_id` varchar(8) NOT NULL,
				`label` varchar(128) NOT NULL,
				`original_id` INT(11) NOT NULL DEFAULT '0',
				`params` TEXT NOT NULL,
				`html` MEDIUMTEXT NOT NULL,
				`css` TEXT NOT NULL,
				`img` varchar(64) DEFAULT NULL,
				`sort_order` MEDIUMINT(5) NOT NULL DEFAULT '0',
				`is_base` TINYINT(1) NOT NULL DEFAULT '1',
				`is_pro`  TINYINT(1) NOT NULL DEFAULT '0',
				`date_created` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,
				PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8;"));
		}

		self::initBaseTables();
		/**
		* Plugin usage statistics
		*/
		if(!dbPtw::exist("@__usage_stat")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE `@__usage_stat` (
			  `id` int(11) NOT NULL AUTO_INCREMENT,
			  `code` varchar(64) NOT NULL,
			  `visits` int(11) NOT NULL DEFAULT '0',
			  `spent_time` int(11) NOT NULL DEFAULT '0',
			  `modify_timestamp` TIMESTAMP DEFAULT CURRENT_TIMESTAMP,
			  UNIQUE INDEX `code` (`code`),
			  PRIMARY KEY (`id`)
			) DEFAULT CHARSET=utf8"));
			dbPtw::query("INSERT INTO `@__usage_stat` (code, visits) VALUES ('installed', 1)");
		}

		if(!dbPtw::exist("@__product_property_codes")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE `@__product_property_codes` (
			  `id` INT NOT NULL AUTO_INCREMENT,
			  `code` VARCHAR(200) CHARACTER SET utf8 COLLATE utf8_bin NOT NULL,
			  `title` varchar(200) DEFAULT NULL,
			  PRIMARY KEY (`id`),
			  UNIQUE INDEX `code_UNIQUE` (`code` ASC)
			) DEFAULT CHARSET=utf8;"));
		} else {
			dbDelta(dbPtw::prepareQuery("
				ALTER TABLE `@__product_property_codes` 
				CHANGE COLUMN `code` `code` VARCHAR(200) CHARACTER SET 'utf8' COLLATE 'utf8_bin' NOT NULL ;
			"));
		}
		if(!dbPtw::exist("@__product_properties")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE `@__product_properties` (
			  `table_id` int(11) NOT NULL,
			  `property_id` int(11) NOT NULL,
			  `order` int(11) NOT NULL DEFAULT '100',
			  KEY `idx_table_id` (`table_id`)
			) DEFAULT CHARSET=utf8;"));
		}
		if(!dbPtw::exist("@__products")) {
			dbDelta(dbPtw::prepareQuery("CREATE TABLE `@__products` (
			  `table_id` int(11) NOT NULL,
			  `product_id` int(11) NOT NULL,
			  `order` int(11) NOT NULL,
			  KEY `idx_table_id` (`table_id`)
			) DEFAULT CHARSET=utf8;"));
		}
		installerDbUpdaterPtw::runUpdate();
		if($current_version && !self::$_firstTimeActivated) {
			self::setUsed();
		}
		update_option($wpPrefix. PTW_DB_PREF. 'db_version', PTW_VERSION);
		add_option($wpPrefix. PTW_DB_PREF. 'db_installed', 1);
	}
	static public function setUsed() {
		update_option(PTW_DB_PREF. 'plug_was_used', 1);
	}
	static public function isUsed() {
		// No welcome page for now
		//return true;
		return (int) get_option(PTW_DB_PREF. 'plug_was_used');
	}
	static public function delete() {
		self::_checkSendStat('delete');
		global $wpdb;
		$wpPrefix = $wpdb->prefix;
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.PTW_DB_PREF."modules`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.PTW_DB_PREF."modules_type`");
		$wpdb->query("DROP TABLE IF EXISTS `".$wpPrefix.PTW_DB_PREF."usage_stat`");
		delete_option($wpPrefix. PTW_DB_PREF. 'db_version');
		delete_option($wpPrefix. PTW_DB_PREF. 'db_installed');
	}
	static public function deactivate() {
		self::_checkSendStat('deactivate');
	}
	static private function _checkSendStat($statCode) {
		if(class_exists('framePtw')
			&& framePtw::_()->getModule('promo')
			&& framePtw::_()->getModule('options')
		) {
			framePtw::_()->getModule('promo')->getModel()->saveUsageStat( $statCode );
			framePtw::_()->getModule('promo')->getModel()->checkAndSend( true );
		}
	}
	static public function update() {
		global $wpdb;
		$wpPrefix = $wpdb->prefix; /* add to 0.0.3 Versiom */
		$currentVersion = get_option($wpPrefix. PTW_DB_PREF. 'db_version', 0);
		if(!$currentVersion || version_compare(PTW_VERSION, $currentVersion, '>')) {
			self::init();
			update_option($wpPrefix. PTW_DB_PREF. 'db_version', PTW_VERSION);
		}
	}
