	static public function installDataByUid($tbl, $uid, $data) {
		$id = (int) dbPtw::get("SELECT id FROM $tbl WHERE unique_id = '$uid' AND original_id = 0 AND is_base = 1", 'one');
		$action = $id ? 'UPDATE' : 'INSERT INTO';
		$values = array();
		foreach($data as $k => $v) {
			$values[] = "$k = \"$v\"";
		}
		$valuesStr = implode(',', $values);
		$query = "$action $tbl SET $valuesStr";
		if($action == 'UPDATE')
			$query .= " WHERE unique_id = '$uid' AND original_id = 0 AND is_base = 1";
		if(dbPtw::query($query)) {
			$dataId = $action == 'UPDATE' ? $id : dbPtw::insertID();
			// Make sure that data - is really unique
			$wrongId = (int) dbPtw::get("SELECT id FROM $tbl WHERE unique_id != '$uid' AND original_id = 0 AND is_base = 1 AND label = '". $data['label']. "'", 'one');
			if($wrongId) {
				dbPtw::query("DELETE FROM $tbl WHERE id = $wrongId");
				dbPtw::query("UPDATE $tbl SET original_id = $dataId WHERE original_id = $wrongId");
			}
			return $dataId;
		}
		return false;
	}

